import React from "react";
import Table from "./Table";

const Row = () => {
  const [rowNum, setRowNum] = React.useState([
    <Table
      key={0}
      grade={this.grade}
      assignment={this.assignment}
      weight={this.weight}
    />,
    <Table key={1} />,
    <Table key={2} />,
  ]);
  let keyCounter = 3; // Initialize a counter variable

  function addRow() {
    setRowNum((prev) => [...prev, <Table key={keyCounter++} />]); // Increment the counter after using it
  }
  function deleteRow() {
    setRowNum((prev) => {
      const updatedRows = [...prev];
      updatedRows.pop(); // Remove the last row
      return updatedRows;
    });
  }

  return (
    <section className="Row--inputs">
      <table>
        <thead>
          <tr>
            <th>Assignment (optional) </th>
            <th>Grade %</th>
            <th>Weight %</th>
          </tr>
        </thead>
        <tbody>
          {rowNum.map(({ row, index }) => (
            <Table />
          ))}
        </tbody>
      </table>
      <button className="addRow" onClick={addRow}>
        + Add row
      </button>
      <button className="addRow" onClick={deleteRow}>
        Delete Row
      </button>
    </section>
  );
};

export default Row;
