import React from "react";
import Table from "./Table";

const Row = () => {
  const [rowNum, setRowNum] = React.useState([<Table />]);
  function addRow() {
    setRowNum((prev) => [...prev, <Table />]);
  }

  return (
    <section className="Row--inputs">
      <table>
        <thead>
          <tr>
            <th>Assignment (optional)</th>
            <th>Grade %</th>
            <th>Weight %</th>
          </tr>
        </thead>
        {rowNum.map((row, index) => (
          <React.Fragment key={index}>{row}</React.Fragment>
        ))}
      </table>
      <button className="addRow" onClick={addRow}>
        + Add row
      </button>
    </section>
  );
};

export default Row;

