import React from "react";
import Table from "./Table";

const Row = (props) => {
  const [rows, setRows] = React.useState([
    { key: 0, grade: "", assignment: "", weight: "" },
  ]);

  // function addRow() {
  //   setRows((prev) => [...prev, <Table key={keyCounter++} />]); // Increment the counter after using it
  // }
  function deleteRow() {
    setRows((prev) => {
      const updatedRows = [...prev];
      updatedRows.pop();
      return updatedRows;
    });
  }

  return (
    <section className="Row--inputs">
      <table>
        <thead>
          <tr>
            <th>Assignment (optional) </th>
            <th>Grade %</th>
            <th>Weight %</th>
          </tr>
        </thead>
        <tbody>
          {rows.map(({ key, grade, assignment, weight }, index) => (
            <Table
              key={key}
              grade={props.grade}
              assignment={props.assignment}
              weight={props.weight}
              handleInputChange={props.handleInputChange}
            />
          ))}
        </tbody>
      </table>
      {/* <button className="addRow" onClick={addRow}>
        + Add row
      </button> */}
      <button className="addRow" onClick={deleteRow}>
        Delete Row
      </button>
      <button className="calculate" onClick={props.handleCalculation}>
        Calculate
      </button>
    </section>
  );
};

export default Row;
