import React from "react";
import Table from "./Table";

const Row = ({
  rows,
  handleGradeChange,
  handleInputChange,
  addRow,
  deleteRow,
}) => {
  return (
    <section className="Row--inputs">
      <table>
        <thead>
          <tr>
            <th>Assignment (optional) </th>
            <th>Grade %</th>
            <th>Weight %</th>
          </tr>
        </thead>
        <tbody>
          {rows.map(({ key, grade, assignment, weight }, index) => (
            <Table
              key={key}
              grade={grade}
              assignment={assignment}
              weight={weight}
              handleInputChange={(e) => handleInputChange(index, e)}
              handleGradeChange={(e) =>
                handleGradeChange(index, e.target.value)
              }
            />
          ))}
        </tbody>
      </table>
      <button className="addRow" onClick={addRow}>
        + Add row
      </button>
      <button className="addRow" onClick={deleteRow}>
        Delete Row
      </button>
      <button className="calculate" onClick={handleCalculation}>
        Calculate
      </button>

      <p>totalGrade: {totalGrade} </p>
      <p>totalWeight: {totalWeight}</p>
    </section>
  );
};

export default Row;
