import React from "react";

const Outputs = ({ grades, weights }) => {
  // Calculate average grade and total weight
  const averageGrade =
    grades.reduce(
      (acc, grade, index) => acc + grade * (weights[index] || 0),
      0
    ) / 100;
  const totalWeight = weights.reduce((acc, weight) => acc + (weight || 0), 0);

  return (
    <section className="outputs">
      <h3>Your Grades</h3>
      <p className="outputs--element">
        Average Grade:{" "}
        <span style={{ fontWeight: "bold" }}>{averageGrade.toFixed(2)} %</span>
      </p>
      <p className="outputs--element">
        Total Weight:{" "}
        <span style={{ fontWeight: "bold" }}>{totalWeight} %</span>
      </p>
    </section>
  );
};

export default Outputs;
