import React, { useState } from "react";

const Row = ({ onGradeChange, onWeightChange }) => {
  const [grade, setGrade] = useState(0);
  const [weight, setWeight] = useState(0);

  // Function to handle grade change
  const handleGradeChange = (event) => {
    const newGrade = parseInt(event.target.value);
    setGrade(newGrade);
    onGradeChange(newGrade); // Pass grade to parent component
  };

  // Function to handle weight change
  const handleWeightChange = (event) => {
    const newWeight = parseInt(event.target.value);
    setWeight(newWeight);
    onWeightChange(newWeight); // Pass weight to parent component
  };

  return (
    <div className="row">
      <input
        type="number"
        placeholder="Enter grade"
        value={grade}
        onChange={handleGradeChange}
      />
      <input
        type="number"
        placeholder="Enter weight"
        value={weight}
        onChange={handleWeightChange}
      />
    </div>
  );
};

export default Row;
