import React from "react";
import Table from "./Table";

const Row = () => {
  const [rowNum, setRowNum] = React.useState([
    { key: 0, assignment: "", grade: "", weight: "" },
    { key: 1, assignment: "", grade: "", weight: "" },
    { key: 2, assignment: "", grade: "", weight: "" },
  ]);

  let keyCounter = 3; // Initialize a counter variable

  function addRow() {
    setRowNum((prev) => [
      ...prev,
      { key: keyCounter++, assignment: "", grade: "", weight: "" },
    ]); // Increment the counter after using it
  }

  function handleAssignmentChange(key, value) {
    setRowNum((prev) =>
      prev.map((row) => (row.key === key ? { ...row, assignment: value } : row))
    );
  }

  function handleGradeChange(key, value) {
    setRowNum((prev) =>
      prev.map((row) => (row.key === key ? { ...row, grade: value } : row))
    );
  }

  function handleWeightChange(key, value) {
    setRowNum((prev) =>
      prev.map((row) => (row.key === key ? { ...row, weight: value } : row))
    );
  }

  return (
    <section className="Row--inputs">
      <table>
        <thead>
          <tr>
            <th>Assignment (optional)</th>
            <th>Grade %</th>
            <th>Weight %</th>
          </tr>
        </thead>
        <tbody>
          {rowNum.map((row) => (
            <Table
              key={row.key}
              assignment={row.assignment}
              grade={row.grade}
              weight={row.weight}
              onAssignmentChange={(e) =>
                handleAssignmentChange(row.key, e.target.value)
              }
              onGradeChange={(e) => handleGradeChange(row.key, e.target.value)}
              onWeightChange={(e) =>
                handleWeightChange(row.key, e.target.value)
              }
            />
          ))}
        </tbody>
      </table>
      <button className="addRow" onClick={addRow}>
        + Add row
      </button>
    </section>
  );
};

export default Row;
