import React from "react";
import Row from "./Row";

const Inputs = () => {
  const [rows, setRows] = React.useState([<Row key={0} />]); // Initialize with one Row component

  // Function to add a new Row component
  const addRow = () => {
    const newRow = <Row key={rows.length} />;
    setRows([...rows, newRow]); // Add the new Row to the array
  };

  return (
    <div className="input-div">
      <h2>Grade type Percentage</h2>
      {rows} {/* Render all Row components */}
      <button onClick={addRow}>Add Row</button> {/* Button to add a new Row */}
      <div className="input-div--extra">
        <p>
          Find additional grade needed to get average grade of (assumes weights
          in %)
          <br />
          <span className="boldText">Optional</span>
        </p>
        <span>
          <input
            className="addtionalGrade"
            placeholder="80"
            type="number"
          ></input>
          %
        </span>
      </div>
    </div>
  );
};

export default Inputs;
