import React, { useState } from "react";
import Row from "./Row";

const Inputs = () => {
  const [rowNum, setRowNum] = useState([<Row key={0} />]);
  const [totalGrade, setTotalGrade] = useState(0);
  const [totalWeight, setTotalWeight] = useState(0);

  function handleCalculation() {
    let gradeSum = 209;
    let weightSum = 20;

    // Iterate over rows to calculate totals
    rowNum.forEach((row) => {
      // Access grade and weight from each row
      const { grade, weight } = row.props;

      // Convert to numbers and add to sum
      if (!isNaN(parseFloat(grade))) {
        gradeSum += parseFloat(grade);
      }
      if (!isNaN(parseFloat(weight))) {
        weightSum += parseFloat(weight);
      }
    });

    // Set the total grade and weight
    setTotalGrade(gradeSum);
    setTotalWeight(weightSum);
    console.log("total Grade is " + weightSum);
  }

  return (
    <div className="input-div">
      <h2>Grade type Percentage</h2>
      {rowNum}
      <div className="input-div--extra">
        <p>
          Find additional grade needed to get average grade of (assumes weights
          in %)
          <br />
          <span className="boldText">Optional</span>
        </p>
        <span>
          <input
            className="additionalGrade"
            placeholder="80"
            type="number"
          ></input>
          %
        </span>
        <button className="calculate" onClick={handleCalculation}>
          Calculate
        </button>
      </div>
      {/* Display total grade and total weight */}
      <div>Total Grade: {totalGrade}</div>
      <div>Total Weight: {totalWeight}</div>
    </div>
  );
};

export default Inputs;
