import React from "react";

const Row = () => {
  const [inputValues, setInputValues] = React.useState([]);

  function addRow() {
    setRowNum((prev) => [...prev, <Row />]);
  }
  const [rowNum, setRowNum] = React.useState([<Row />]);

  return (
    <section className="Row--inputs">
      <table>
        <thead>
          <tr>
            <th>Assignment</th>
            <th>Grade</th>
            <th>Weight</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>
              <input></input>
            </td>
            <td>
              <input></input>
            </td>
            <td>
              <input></input>
            </td>
          </tr>
        </tbody>
      </table>
      <button className="addRow" onClick={addRow}>
        + Add row
      </button>
    </section>
  );
};

export default Row;

