import React from "react";
import Table from "./Table";

const Inputs = ({ updateGrades }) => {
  const [tableData, setTableData] = React.useState();

  // Initialize extraField state as an empty object
  const [extraField, setExtraField] = React.useState();

  // Handle input change for extraField
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setExtraField((prev) => ({
      ...prev,
      [name]: value,
    }));
  };
  return (
    <div className="input-div">
      <h2>Grade type Percentage</h2>
      <Table
        updateGrades={updateGrades}
        tableData={tableData}
        setTableData={setTableData}
      />
      <div className="input-div--extra">
        <p>
          {" "}
          Find additional grade needed to get average grade of (assumes weights
          in %)
          <br />
          <span className="boldText">Optional</span>
        </p>
        <span>
          <input
            className="additionalGrade"
            placeholder="80"
            type="number"
            name="extra" // Use "extra" as the name for the input field
            value={extraField.extra} // Access extraField.extra for the value
            onChange={handleInputChange}
          ></input>
          %
        </span>
      </div>
    </div>
  );
};

export default Inputs;
