import React from "react";
import Table from "./Table";

const Row = () => {
  const [tableCollection, setTableCollection] = React.useState([
    <Table key={0} />,
    <Table key={1} />,
    <Table key={2} />,
  ]);
  let keyCounter = 3; // Initialize a counter variable

  function addRow() {
    setRowNum((prev) => [...prev, <Table key={keyCounter++} />]); // Increment the counter after using it
  }
  function deleteRow() {
    setRowNum((prev) => {
      const updatedRows = [...prev];
      updatedRows.pop();
      return updatedRows;
    });
  }

  const handleInputChange = (index, e) => {
    const { name, value } = e.target;
    setRowNum((prev) => {
      const updatedRows = [...prev];
      updatedRows[index] = { ...updatedRows[index], [name]: value };
      return updatedRows;
    });
  };

  return (
    <section className="Row--inputs">
      <table>
        <thead>
          <tr>
            <th>Assignment (optional) </th>
            <th>Grade %</th>
            <th>Weight %</th>
          </tr>
        </thead>
        <tbody>
          {tableCollection.map(({ key, grade, assignment, weight }, index) => (
            <Table
              key={key}
              grade={grade}
              assignment={assignment}
              weight={weight}
              handleInputChange={(e) => handleInputChange(index, e)}
            />
          ))}
        </tbody>
      </table>
      <button className="addRow" onClick={addRow}>
        + Add row
      </button>
      <button className="addRow" onClick={deleteRow}>
        Delete Row
      </button>
    </section>
  );
};

export default Row;
