import React from "react";

const Table = ({
  assignment,
  grade,
  weight,
  onAssignmentChange,
  onGradeChange,
  onWeightChange,
}) => {
  const [inputs, setInputs] = React.useState({
    assignment: "home",
    grade: "",
    weight: "",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputs((prevInputs) => ({
      ...prevInputs,
      [name]: value,
    }));
  };

  return (
    <tr>
      <td>
        <input
          type="text"
          name="assignment"
          value={inputs.input1}
          onChange={handleInputChange}
          placeholder="e.g Homework"
        />
      </td>
      <td>
        <input
          className="gradeInput"
          type="number"
          name="grade"
          value={inputs.input2}
          onChange={handleInputChange}
        />
      </td>
      <td>
        <input
          className="weightInput"
          type="number"
          name="weight"
          value={inputs.input3}
          onChange={handleInputChange}
        />
      </td>
    </tr>
  );
};

export default Table;
