import React, { useState } from "react";
import "./App.css";

function App() {
  const [grades, setGrades] = useState([]);
  const [weights, setWeights] = useState([]);
  const [gradeInput, setGradeInput] = useState("");
  const [weightInput, setWeightInput] = useState("");
  const [totalGrade, setTotalGrade] = useState(0);

  const addGrade = () => {
    if (gradeInput && weightInput) {
      setGrades([...grades, parseFloat(gradeInput)]);
      setWeights([...weights, parseFloat(weightInput)]);
      setGradeInput("");
      setWeightInput("");
    }
  };

  const calculateTotalGrade = () => {
    let total = 0;
    let totalWeight = 0;
    for (let i = 0; i < grades.length; i++) {
      total += grades[i] * weights[i];
      totalWeight += weights[i];
    }
    setTotalGrade(total / totalWeight);
  };

  return (
    <div className="App">
      <h1>Grade Calculator</h1>
      <div className="grades-container">
        {grades.map((grade, index) => (
          <div key={index} className="grade-item">
            <span>
              Grade {index + 1}: {grade}
            </span>
            <span>Weight: {weights[index]}</span>
          </div>
        ))}
      </div>
      <div className="input-container">
        <input
          type="number"
          placeholder="Grade"
          value={gradeInput}
          onChange={(e) => setGradeInput(e.target.value)}
        />
        <input
          type="number"
          placeholder="Weight"
          value={weightInput}
          onChange={(e) => setWeightInput(e.target.value)}
        />
        <button onClick={addGrade}>Add Grade</button>
      </div>
      <button onClick={calculateTotalGrade}>Calculate Total Grade</button>
      {totalGrade > 0 && <h2>Total Grade: {totalGrade.toFixed(2)}</h2>}
    </div>
  );
}

export default App;
