import React, { useState } from "react";

const Row = () => {
  const [inputValues, setInputValues] = useState({
    assignments: "",
    grade: "",
    weight: "",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputValues({
      ...inputValues,
      [name]: value,
    });
  };

  return (
    <div className="inputs">
      <div className="assignment-inputs">
        <label>Assignments</label>
        <input
          className="input assName"
          name="assignments"
          value={inputValues.assignments}
          onChange={handleInputChange}
        ></input>
      </div>
      <div className="grade-inputs">
        <label>Grade %</label>
        <input
          className="input gradeNum"
          name="grade"
          value={inputValues.grade}
          onChange={handleInputChange}
        ></input>
      </div>
      <div className="weight-inputs">
        <label>Weight %</label>
        <input
          className="input weightNum"
          name="weight"
          value={inputValues.weight}
          onChange={handleInputChange}
        ></input>
      </div>
    </div>
  );
};

export default Row;
