import React from "react";
import Table from "./Table";

const Row = () => {
  const [rowCount, setRowCount] = React.useState(1);

  function addRow() {
    setRowCount((prevRowCount) => prevRowCount + 1);
  }

  return (
    <section className="Row--inputs">
      <table>
        <thead>
          <tr>
            <th>Assignment</th>
            <th>Grade</th>
            <th>Weight</th>
          </tr>
        </thead>
        <tbody>
          {[...Array(rowCount)].map((_, index) => (
            <Table key={index} />
          ))}
        </tbody>
      </table>
      <button className="addRow" onClick={addRow}>
        + Add row
      </button>
    </section>
  );
};

export default Row;

