import React from "react";
import Table from "./Table";

// Import necessary components and React

const Row = () => {
  const [rowNum, setRowNum] = React.useState([
    { key: 0, grade: "", assignment: "", weight: "" },
    { key: 1, grade: "", assignment: "", weight: "" },
    { key: 2, grade: "", assignment: "", weight: "" },
  ]);
  let keyCounter = 3; // Initialize a counter variable

  function addRow() {
    setRowNum((prev) => [
      ...prev,
      { key: keyCounter++, grade: "", assignment: "", weight: "" },
    ]); // Increment the counter after using it
  }

  function deleteRow() {
    setRowNum((prev) => prev.slice(0, prev.length - 1)); // Remove the last row
  }

  const handleInputChange = (index, e) => {
    const { name, value } = e.target;
    setRowNum((prev) => {
      const updatedRows = [...prev];
      updatedRows[index] = { ...updatedRows[index], [name]: value };
      return updatedRows;
    });
  };

  return (
    <section className="Row--inputs">
      <table>
        <thead>
          <tr>
            <th>Assignment (optional) </th>
            <th>Grade %</th>
            <th>Weight %</th>
          </tr>
        </thead>
        <tbody>
          {rowNum.map(({ key, grade, assignment, weight }, index) => (
            <Table
              key={key}
              grade={grade}
              assignment={assignment}
              weight={weight}
              handleInputChange={(e) => handleInputChange(index, e)}
            />
          ))}
        </tbody>
      </table>
      <button className="addRow" onClick={addRow}>
        + Add row
      </button>
      <button className="deleteRow" onClick={deleteRow}>
        Delete Row
      </button>
    </section>
  );
};

export default Row;
