import React from "react";
import Row from "./Row";

const Table = (props) => {
  const [rows, setRows] = React.useState([
    { key: 0, grade: "", assignment: "", weight: "" },
    { key: 1, grade: "", assignment: "", weight: "" },
    { key: 2, grade: "", assignment: "", weight: "" },
  ]);
  let keyCounter = 3; // Initialize a counter variable

  const [totalGrade, setTotalGrade] = React.useState(0);
  const [totalWeight, setTotalWeight] = React.useState(0);
  const [calculateClicked, setCalculateClicked] = React.useState(false);

  function handleCalculation() {
    let gradeSum = 0;
    let weightSum = 0;

    rows.forEach(({ grade, weight }) => {
      const numericGrade = parseFloat(grade);
      const numericWeight = parseFloat(weight);

      if (!isNaN(numericGrade) && !isNaN(numericWeight)) {
        gradeSum += numericGrade * numericWeight;
        weightSum += numericWeight;
      }
    });

    const finalAvg = gradeSum / weightSum;

    // Update state only when the button is clicked
    if (calculateClicked) {
      setTotalGrade(finalAvg);
      setTotalWeight(weightSum);
    }
  }

  const handleInputChange = (index, e) => {
    const { name, value } = e.target;
    setRows((prev) => {
      const updatedRows = [...prev];
      updatedRows[index] = { ...updatedRows[index], [name]: value };

      // Call updateGrades with the updated rows
      updateGrades(updatedRows);

      return updatedRows;
    });
  };

  return (
    <section className="Row--inputs">
      <table>{/* Table content */}</table>
      <button className="calculate" onClick={() => setCalculateClicked(true)}>
        Calculate
      </button>

      {calculateClicked && (
        <>
          <p>totalGrade: {totalGrade} </p>
          <p>totalWeight: {totalWeight}</p>
        </>
      )}
    </section>
  );
};

export default Table;
